                     MyDocument.Pages.Add(MyPage)

                     ' Add a label to the page
                     MyPage.Elements.Add(New Label("My PDF Document", 0, 0, 512, 40, Font.Helvetica, 30, TextAlign.Center))

                     ' Create a memory stream object to save to
