example.code:
using System;
using Foxit.PDF;
using Foxit.PDF.PageElements;

class MyClass
{
    static void Main()
    {
        // Create a document and set it's properties
        Foxit.PDF.Document document = new Foxit.PDF.Document();

        // Create a page to add to the document
        Foxit.PDF.Page page = new Foxit.PDF.Page( PageSize.Letter, PageOrientation.Portrait, 54.0f );

        // Create a Label to add to the page
        string text = "JavaScript Example";
        Label label = new Label( text, 0, 0, 504, 100, Font.Helvetica, 18, TextAlign.Center );

        // Add the JavaScript alert to the Document
        document.JavaScripts.Add( new DocumentJavaScript( "HelloWorld", "app.alert(\"Hello World!!\")" ) );

        // Add label to page
        page.Elements.Add( label );

        // Add page to document
        document.Pages.Add( page );

        // Save the PDF document
        document.Draw( @"C:\MyDocument.pdf" );
    }
}

using System;
using Foxit.PDF;
using Foxit.PDF.PageElements;

class MyClass
{
    static void Main()
    {
        // Create a document and set it's properties
        Foxit.PDF.Document document = new Foxit.PDF.Document();

        // Create a page to add to the document
        Foxit.PDF.Page page = new Foxit.PDF.Page( PageSize.Letter, PageOrientation.Portrait, 54.0f );

        // Create a Label to add to the page
        string text = "JavaScript Example";
        Label label = new Label( text, 0, 0, 504, 100, Font.Helvetica, 18, TextAlign.Center );

        // Add the JavaScript alert to the Document
        document.JavaScripts.Add( new DocumentJavaScript( "HelloWorld", "app.alert(\"Hello World!!\")" ) );

        // Add label to page
        page.Elements.Add( label );

        // Add page to document
        document.Pages.Add( page );

        // Save the PDF document
        document.Draw( @"C:\MyDocument.pdf" );
    }
}

