            using Foxit.PDF;
            using Foxit.PDF.Merger;

            class MyClass
            {
                static void Main()
                {
                    // Create a PDF Document
                    MergeDocument document = new MergeDocument(@"C:\DuplicateImagedata.pdf");

                    // Set document optimization
                    document.Optimization = new DocumentOptimization();

                    // Save the PDF
                    document.Draw( @"C:\MyDocument.pdf" );
                }
            }

This example shows how to create a package PDF. 


             Imports System
             Imports System.IO
             Imports Foxit.PDF
             Imports Foxit.PDF.IO

             Module MyModule

                 Sub Main()

                     ' Create a PDF document.
                     Dim MyDocument As Document = New Document 

                     ' File streams used for creating the instance of embedded file.
                     Dim fileStream1 As FileStream = New FileStream("C:\DocumentA.pdf",FileMode.Open,FileAccess.Read,FileShare.ReadWrite)
                     Dim fileStream2 As FileStream  = New FileStream("C:\DocumentB.pdf",FileMode.Open,FileAccess.Read,FileShare.ReadWrite)

                     ' Byte array of the file by reading the file.
                     Dim  filebyte(fileStream2.Length) As  byte 
                     fileStream2.Read(filebyte, 0, filebyte.Length )

                     ' Create 3 instances of embedded file using all the available constructors.
                     Dim embeddedFile1 As EmbeddedFile  = New EmbeddedFile("C:\DocumentC.pdf")
                     Dim embeddedFile2 As EmbeddedFile  = New EmbeddedFile( fileStream1, "DocumentA.pdf", DateTime.Now )
                     Dim embeddedFile3 As  EmbeddedFile  = New EmbeddedFile( filebyte, "DocumentB.pdf", DateTime.Now )

                     ' Add the embedded files to the embedded file list.
                     MyDocument.EmbeddedFiles.Add( embeddedFile1 )
                     MyDocument.EmbeddedFiles.Add( embeddedFile2 )
                     MyDocument.EmbeddedFiles.Add( embeddedFile3 )

                    ' Create a page and add it to the document.
                     Dim MyPage As Page = New Page
                     MyDocument.Pages.Add(MyPage)

                     ' Create a document package with attachment layout detailed.
                     MyDocument.Package = New DocumentPackage(AttachmentLayout.Detailed)

                     ' Add a label to the page.
                     MyPage.Elements.Add(New Label("This is a Cover page for Package PDF", 0, 0, 512, 40, Font.Helvetica, 30, TextAlign.Center))

                     ' Save the PDF document.
                     MyDocument.Draw("C:\MyDocument.pdf")

                 End Sub
             End Module


             using System;
             using System.IO;
             using Foxit.PDF;
             using Foxit.PDF.IO;

             class MyClass
             {
                 static void Main()
                 {
                     // Create a PDF document.
                     Document document = new Document();

                     // File streams used for creating the instance of embedded file.
                     FileStream fileStream1 = new FileStream(@"C:\DocumentA.pdf",FileMode.Open,FileAccess.Read,FileShare.ReadWrite);
            		    FileStream fileStream2 = new FileStream(@"C:\DocumentB.pdf",FileMode.Open,FileAccess.Read,FileShare.ReadWrite);

                     // Byte array of the file by reading the file.
                     byte[] filebyte = new byte[fileStream2.Length];
                     fileStream2.Read(filebyte, 0, filebyte.Length );

                     // Create 3 instances of embedded file using all the available constructors.
                     EmbeddedFile embeddedFile1 = new EmbeddedFile(@"C:\DocumentC.pdf");
                     EmbeddedFile embeddedFile2 = new EmbeddedFile( fileStream1, "DocumentA.pdf", DateTime.Now );
                     EmbeddedFile embeddedFile3 = new EmbeddedFile( filebyte, "DocumentB.pdf", DateTime.Now );

                     // Add the embedded files to the embedded file list.
                     document.EmbeddedFiles.Add( embeddedFile1 );
                     document.EmbeddedFiles.Add( embeddedFile2 );
                     document.EmbeddedFiles.Add( embeddedFile3 );

                     // Create a page and add it to the document.
                     Page page = new Page();
                     document.Pages.Add( page );

                     // Create a document package with attachment layout detailed.
                     document.Package = new DocumentPackage(AttachmentLayout.Detailed);

                     // Add a label to the page 
                     page.Elements.Add( new Label("This is a Cover page for Package PDF", 0, 0, 512, 40, Font.Helvetica, 30, TextAlign.Center ) );

                     // Save the PDF document
                     document.Draw( @"C:\MyDocument.pdf" );
                 }
             }

The following example shows how to use document reader events. 


            Imports Foxit.PDF
            Imports Foxit.PDF.PageElements.Forms
            Imports Foxit.PDF.PageElements

            Module MyModule

                 Sub Main()	

                    ' Create a PDF Document
                    Dim MyDocument As Document = New Document()

                    ' Create a page and add it to the document
                    Dim MyPage As Page = New Page()
                    MyDocument.Pages.Add(MyPage)

                    ' Create labels and add it to the page
                    Dim MyLabel1 As Label = New Label("Document Additional Actions", 0, 50, 400, 50, Font.Courier, 20)
                    Dim MyLabel2 As Label = New Label("Enter some text in the text field and save the file to see the document action :", 0, 150, 400, 50, Font.Courier, 15)
                    MyPage.Elements.Add(MyLabel1)
                    MyPage.Elements.Add(MyLabel2)

                    ' Create a text field and add it to the page
                    Dim MytextField As Foxit.PDF.PageElements.Forms.TextField  = New Foxit.PDF.PageElements.Forms.TextField("Text1", 385, 155, 100, 30)
                    MyPage.Elements.Add(MytextField)

                    ' Create a java script action and assign it to the document reader event.
                    Dim MyAction As JavaScriptAction = New JavaScriptAction("app.alert(""Hello your text Saved!!"")")
                    MyDocument.ReaderEvents.WillSave = MyAction

                    ' Save the PDF
                    MyDocument.Draw("C:\MyDocument.pdf")

                End Sub
            End Module


            using Foxit.PDF;
            using Foxit.PDF.PageElements.Forms;
            using Foxit.PDF.PageElements;

            class MyClass
            {
                static void Main(string[] args)
                {
                    // Create a PDF Document
                    Document document = new Document();

                    // Create a page and add it to the document
                    Page page = new Page();
                    document.Pages.Add(page);

                    // Create labels and add it to the page
                    Label label1 = new Label("Document Additional Actions", 0, 50, 400, 50, Font.Courier, 20);
                    Label label2 = new Label("Enter some text in the text field and save the file to see the document action :", 0, 150, 400, 50, Font.Courier, 15);
                    page.Elements.Add(label1);
                    page.Elements.Add(label2);

                    // Create a text field and add it to the page
                    Foxit.PDF.PageElements.Forms.TextField textField = new Foxit.PDF.PageElements.Forms.TextField("Text1", 385, 155, 100, 30);
                    page.Elements.Add(textField);

                    // Create a java script action and assign it to the document reader event.
                    JavaScriptAction action = new JavaScriptAction("app.alert(\"Hello your text Saved!!\")");
                    document.ReaderEvents.WillSave = action;

                    // Save the PDF
                    document.Draw(@"c:\MyDocument.pdf");
                }
            }

This example shows how to embed files(attachments) in to a document.


             Imports System
             Imports System.IO
             Imports Foxit.PDF
             Imports Foxit.PDF.PageElements

             Module MyModule

                 Sub Main()

                     ' Create a PDF Document
                     Dim MyDocument As Document = New Document 

                     ' FileStreams used for creating the instance of EmbeddedFile
                     Dim fileStream1 As FileStream = New FileStream("C:\DocumentA.pdf",FileMode.Open,FileAccess.Read,FileShare.ReadWrite)
                     Dim fileStream2 As FileStream  = New FileStream("C:\DocumentB.pdf",FileMode.Open,FileAccess.Read,FileShare.ReadWrite)

                     ' byte array of the file by reading the file.
                     Dim  filebyte(fileStream2.Length) As  byte 
                     fileStream2.Read(filebyte, 0, filebyte.Length )

                     ' Created 3 instances of EmbeddedFile using all the available constructors.
                     Dim  embeddedFile1 As EmbeddedFile  = New EmbeddedFile("C:\DocumentC.pdf")
                     Dim embeddedFile2 As EmbeddedFile  = New EmbeddedFile( fileStream1, "DocumentA.pdf", DateTime.Now )
                     Dim embeddedFile3 As  EmbeddedFile  = New EmbeddedFile( filebyte, "DocumentB.pdf", DateTime.Now )

                     ' Added the embeddedFiles to the EmbeddedFileList of the document class.
                     MyDocument.EmbeddedFiles.Add( embeddedFile1 )
                     MyDocument.EmbeddedFiles.Add( embeddedFile2 )
                     MyDocument.EmbeddedFiles.Add( embeddedFile3 )

                     ' Set the PageMode to the ShowAttachments
                     MyDocument.InitialPageMode = PageMode.ShowAttachments

                     ' Create a Page and add it to the document
                     Dim MyPage As Page = New Page
                     MyDocument.Pages.Add(MyPage)

                     ' Add a label to the page
                     MyPage.Elements.Add(New Label("My PDF Document", 0, 0, 512, 40, Font.Helvetica, 30, TextAlign.Center))

                     ' Save the PDF document
                     MyDocument.Draw("C:\MyDocument.pdf")

                 End Sub
             End Module


             using System;
             using System.IO;
             using Foxit.PDF;
             using Foxit.PDF.PageElements;

             class MyClass
             {
                 static void Main()
                 {
                     // Create a PDF Document
                     Document document = new Document();

                     // FileStreams used for creating the instance of EmbeddedFile
                     FileStream fileStream1 = new FileStream(@"C:\DocumentA.pdf",FileMode.Open,FileAccess.Read,FileShare.ReadWrite);
            		    FileStream fileStream2 = new FileStream(@"C:\DocumentB.pdf",FileMode.Open,FileAccess.Read,FileShare.ReadWrite);

                     // byte array of the file by reading the file.
                     byte[] filebyte = new byte[fileStream2.Length];
                     fileStream2.Read(filebyte, 0, filebyte.Length );

                     // Created 3 instances of EmbeddedFile using all the available constructors.
                     EmbeddedFile embeddedFile1 = new EmbeddedFile(@"C:\DocumentC.pdf");
                     EmbeddedFile embeddedFile2 = new EmbeddedFile( fileStream1, "DocumentA.pdf", DateTime.Now );
                     EmbeddedFile embeddedFile3 = new EmbeddedFile( filebyte, "DocumentB.pdf", DateTime.Now );

                     // Added the embeddedFiles to the EmbeddedFileList of the document class.
                     document.EmbeddedFiles.Add( embeddedFile1 );
                     document.EmbeddedFiles.Add( embeddedFile2 );
                     document.EmbeddedFiles.Add( embeddedFile3 );

                     // Set the PageMode to the ShowAttachments
                     document.InitialPageMode = PageMode.ShowAttachments;

                     // Create a Page and add it to the document
                     Page page = new Page();
                     document.Pages.Add( page );

                     // Add a label to the page 
                     page.Elements.Add( new Label( "My PDF Document", 0, 0, 512, 40, Font.Helvetica, 30, TextAlign.Center ) );

                     // Save the PDF document
                     document.Draw( @"C:\MyDocument.pdf" );
                 }     
             }

This example shows how to create an EvenOddTemplate and add it to the document.


            Imports System
            Imports Foxit.PDF

            Module MyModule

                Sub Main()

                    ' Create a PDF Document
                    Dim MyDocument As Document = New Document

                    ' Add 5 blank pages to the document
                    MyDocument.Pages.Add(New Page(PageSize.Letter))
                    MyDocument.Pages.Add(New Page(PageSize.Letter))
                    MyDocument.Pages.Add(New Page(PageSize.Letter))
                    MyDocument.Pages.Add(New Page(PageSize.Letter))
                    MyDocument.Pages.Add(New Page(PageSize.Letter))

                    ' Create an even odd template and add elements to it
                    Dim MyTemplate As EvenOddTemplate = New EvenOddTemplate
                    MyTemplate.EvenElements.Add(New Label("Even Header", 0, 0, 200, 12))
                    MyTemplate.OddElements.Add(New Label("Odd Header", 0, 0, 200, 12)) 
                    MyTemplate.Elements.Add(New Label("Footer", 0, 680, 200, 12))

                    ' Add the template to the document
                    MyDocument.Template = MyTemplate

                    ' Save the PDF document
                    MyDocument.Draw("C:\MyDocument.pdf")

                End Sub
            End Module


            using System;
            using Foxit.PDF;

            class MyClass
            {
                static void Main()
                {
                    // Create a PDF Document
                    Document document = new Document();

                    // Add 5 blank pages to the document
                    document.Pages.Add( new Page( PageSize.Letter ) );
                    document.Pages.Add( new Page( PageSize.Letter ) );
                    document.Pages.Add( new Page( PageSize.Letter ) );
                    document.Pages.Add( new Page( PageSize.Letter ) );
                    document.Pages.Add( new Page( PageSize.Letter ) );

                    // Create an even odd template and add elements to it
                    EvenOddTemplate template = new EvenOddTemplate();
                    template.EvenElements.Add( new Label( "Even Header", 0, 0, 200, 12 ) );
                    template.OddElements.Add( new Label( "Odd Header", 0, 0, 200, 12 ) );
                    template.Elements.Add( new Label( "Footer", 0, 680, 200, 12 ) );

                    // Add the template to the document
                    document.Template = template;

                    // Save the PDF document
                    document.Draw( @"C:\MyDocument.pdf" );
                }
            }

