The following example will place a rectangle and several lines into a group object and then add that group to the page.


             Imports System
             Imports Foxit.PDF
             Imports Foxit.PDF.PageElements

             Module MyModule

                 Sub Main()

                     ' Create a PDF Document
                     Dim MyDocument As Document = New Document 

                     ' Create a Page and add it to the document
                     Dim MyPage As Page = New Page
                     MyDocument.Pages.Add(MyPage)

                     ' Create a group
                     Dim MyGroup As Group = New Group

                     ' Add page elements to the group 
                     MyGroup.Add(New Rectangle(0, 0, 200, 200, 3))
                     MyGroup.Add(New Line(0, 100, 100, 0, 3))
                     MyGroup.Add(New Line(100, 0, 200, 100, 3))
                     MyGroup.Add(New Line(200, 100, 100, 200, 3))
                     MyGroup.Add(New Line(100, 200, 0, 100, 3))

                     ' Add the group to the page
                     MyPage.Elements.Add(MyGroup)

                     ' Save the PDF
                     MyDocument.Draw("C:\MyDocument.pdf")

                 End Sub
             End Module


             using System;
             using Foxit.PDF;
             using Foxit.PDF.PageElements;

             class MyClass
             {
                 static void Main()
                 {
                     // Create a PDF Document
                     Document document = new Document();

                     // Create a Page and add it to the document
                     Page page = new Page();
                     document.Pages.Add( page );

                     // Create a group
                     Group mygroup = new Group();

                     // Add page elements to the group
                     mygroup.Add( new Rectangle( 0, 0, 200, 200, 3 ) );
                     mygroup.Add( new Line( 0, 100, 100, 0, 3 ) );
                     mygroup.Add( new Line( 100, 0, 200, 100, 3 ) );
                     mygroup.Add( new Line( 200, 100, 100, 200, 3 ) );
                     mygroup.Add( new Line( 100, 200, 0, 100, 3 ) );

                     // Add the group to the page
                     page.Elements.Add( mygroup );	

                     // Save the PDF
                     document.Draw( @"C:\MyDocument.pdf" );
                 }
             }

The following example will place a rectangle and several lines into a group object and then add that group to the page.


             Imports System
             Imports Foxit.PDF
             Imports Foxit.PDF.PageElements

             Module MyModule

                 Sub Main()

                     ' Create a PDF Document
                     Dim MyDocument As Document = New Document 

                     ' Create a Page and add it to the document
                     Dim MyPage As Page = New Page
                     MyDocument.Pages.Add(MyPage)

                     ' Create an area group
                     Dim MyGroup As AreaGroup = New AreaGroup(200, 200)

                     ' Add page elements to the group 
                     MyGroup.Add(New Rectangle(0, 0, 200, 200, 3))
                     MyGroup.Add(New Line(0, 100, 100, 0, 3))
                     MyGroup.Add(New Line(100, 0, 200, 100, 3))
                     MyGroup.Add(New Line(200, 100, 100, 200, 3))
                     MyGroup.Add(New Line(100, 200, 0, 100, 3))

                     ' Add the group to the page
                     MyPage.Elements.Add(MyGroup)

                     ' Save the PDF
                     MyDocument.Draw("C:\MyDocument.pdf")

                 End Sub
             End Module


             using System;
             using Foxit.PDF;
             using Foxit.PDF.PageElements;

             class MyClass
             {
                 static void Main()
                 {
                     // Create a PDF Document
                     Document document = new Document();

                     // Create a Page and add it to the document
                     Page page = new Page();
                     document.Pages.Add( page );

                     // Create an area group
                     AreaGroup group1 = new AreaGroup( 200, 200 );

                     // Add page elements to the group
             	group1.Add( new Rectangle( 0, 0, 200, 200, 3 ) );
             	group1.Add( new Line( 0, 100, 100, 0, 3 ) );
             	group1.Add( new Line( 100, 0, 200, 100, 3 ) );
             	group1.Add( new Line( 200, 100, 100, 200, 3 ) );
             	group1.Add( new Line( 100, 200, 0, 100, 3 ) );

                     // Add the group to the page
                     page.Elements.Add( group1 );	

                     // Save the PDF
                     document.Draw( @"C:\MyDocument.pdf" );
                 }
             }

The following example will place an image on the page making it the background for that page.


             Imports System
             Imports Foxit.PDF
             Imports Foxit.PDF.PageElements

             Module MyModule

                 Sub Main()
                     ' Create a PDF Document
                     Dim MyDocument As Document = New Document 

                     ' Create a Page and add it to the document
                     Dim MyPage As Page = New Page
                     MyDocument.Pages.Add(MyPage)

                     ' Create a background image
                     Dim MyBackgroundImage As BackgroundImage = New BackgroundImage("C:\MyImage.jpg")

                     ' Place the image with in the page margins 
                     MyBackgroundImage.UseMargins = True

                     ' Add the background image to the page
                     MyPage.Elements.Add(MyBackgroundImage)

                     ' Save the PDF
                     MyDocument.Draw("C:\MyDocument.pdf")

                 End Sub
             End Module


             using System;
             using Foxit.PDF;
             using Foxit.PDF.PageElements;

             class MyClass
             {
                 static void Main()
                 {
                     // Create a PDF Document
                     Document document = new Document();

                     // Create a page and add it to the document
                     Page page = new Page();
                     document.Pages.Add( page );

                     // Create a background image
                     BackgroundImage backgroundImage = new BackgroundImage( @"C:\MyImage.jpg" );

                     // Place the image with in the page margins
                     backgroundImage.UseMargins = true;

                     // Add the background image to the page
                     page.Elements.Add( backgroundImage );	

                     //Save the PDF
                     document.Draw( @"C:\MyDocument.pdf" );
                 }
             }

