             using Foxit.PDF;
             using Foxit.PDF.PageElements;
             using Foxit.PDF.PageElements.Charting;
             using Foxit.PDF.PageElements.Charting.Series;
             using Foxit.PDF.PageElements.Charting.Axes;

             class MyClass
             {
