                     Title title2 = new Title("Year - 2007");
                     chart.HeaderTitles.Add(title1);
                     chart.HeaderTitles.Add(title2);

                     // Create percentage xAxis 
                     PercentageXAxis xAxis = new PercentageXAxis();

