                     MyAreaSeries3.Values.Add(4, New DateTime(2007, 2, 1))
                     MyAreaSeries3.Values.Add(6, New DateTime(2007, 3, 1))
                     MyAreaSeries3.Values.Add(9, New DateTime(2007, 4, 1))

                     'Add date time area series to the plot area
