                     seriesElement2.Values.Add(4, new DateTime(2007, 1, 1));
                     seriesElement2.Values.Add(2, new DateTime(2007, 2, 1));
                     seriesElement2.Values.Add(5, new DateTime(2007, 3, 1));
                     seriesElement2.Values.Add(8, new DateTime(2007, 4, 1));
