                     MySeriesElement1.Values.Add(7, New DateTime(2007, 2, 1))
                     MySeriesElement1.Values.Add(9, New DateTime(2007, 3, 1))
                     MySeriesElement1.Values.Add(6, New DateTime(2007, 4, 1))
