                     seriesElement1.Values.Add(new DateTime100PercentStackedColumnValue(7, new DateTime(2007, 2, 1)));
                     seriesElement1.Values.Add(new DateTime100PercentStackedColumnValue(4, new DateTime(2007, 3, 1)));
