                     areaSeries3.Values.Add(new DateTimeAreaValue(6, new DateTime(2007, 3, 1)));
                     areaSeries3.Values.Add(new DateTimeAreaValue(9, new DateTime(2007, 4, 1)));

                     // Add date time area series to the plot area
