                     ' Add date time column series to the plot area
                     MyPlotArea.Series.Add(MyColumnSeries1)
                     MyPlotArea.Series.Add(MyColumnSeries2)
                     MyPlotArea.Series.Add(MyColumnSeries3)

                     ' Create a title and add it to the yAxis
