                     IndexedAreaSeries areaSeries3 = new IndexedAreaSeries("Website C", xAxis, yAxis);
                     areaSeries3.Values.Add(new IndexedAreaValue(2, 0));
                     areaSeries3.Values.Add(new IndexedAreaValue(4, 1));
