                            page = new Page(PageSize.A4);
                            page.Elements.Add(list);
                            document.Pages.Add(page);
                            list = list.GetOverFlowList(50,50);
                        }           

                        //save the PDF.
                        document.Draw(@"c:\MyDocument.pdf");
             	}
             }

The following example shows how to use page reader events. 


            Imports Foxit.PDF
            Imports Foxit.PDF.PageElements

            Module MyModule

                 Sub Main()	

                     ' Create a PDF Document
                     Dim MyDocument As Document = New Document()

                     ' Create a pages and add it to the document
                     Dim MyPage As Page = New Page()
            	 Dim MyPage1 As Page = New Page()	
                     MyDocument.Pages.Add(MyPage)
            	 MyDocument.Pages.Add(MyPage1)

                    ' Create labels and add it to the page
                    Dim MyLabel1 As Label  = New Label("Page Additional Actions", 50, 50, 400, 50, Font.Courier, 20)
                    Dim MyLabel2 As Label  = New Label("Go to the second page to see the page open action", 50, 150, 300, 50, Font.Courier, 15)
                    Dim MyLabel3 As Label  = New Label("Second page", 50, 100, 300, 50, Font.Courier, 15)
                    MyPage.Elements.Add(MyLabel1)
                    MyPage.Elements.Add(MyLabel2)
                    MyPage.Elements.Add(MyLabel3)

                    ' Create a java script action and assign to the page reader event.
                    Dim MyAction As JavaScriptAction = New JavaScriptAction("app.alert(\"Welcome to the second page!!\")")
                    MyPage1.ReaderEvents.Open = MyAction

                    ' Save the PDF
                    MyDocument.Draw("C:\MyDocument.pdf")

              End Sub
            End Module


            using Foxit.PDF;
            using Foxit.PDF.PageElements;

            class MyClass
            {
                static void Main(string[] args)
                {
                    // Create a PDF Document
                    Document document = new Document();

                    // Create pages and add it to the document
                    Page page = new Page();
                    Page page1 = new Page();
                    document.Pages.Add(page);
                    document.Pages.Add(page1);

                    // Create labels and add it to the page
                    Label label1 = new Label("Page Additional Actions", 50, 50, 400, 50, Font.Courier, 20);
                    Label label2 = new Label("Go to the second page to see the page open action", 50, 150, 300, 50, Font.Courier, 15);
                    Label label3 = new Label("Second page", 50, 100, 300, 50, Font.Courier, 15);
                    page.Elements.Add(label1);
                    page.Elements.Add(label2);
                    page1.Elements.Add(label3);

                    // Create a java script action and assign to the page reader event.
                    JavaScriptAction action = new JavaScriptAction("app.alert(\"Welcome to the second page!!\")");
                    page1.ReaderEvents.Open = action;

                    // Save the PDF
                    document.Draw(@"c:\MyDocument.pdf");
                }
            }

example.code:
using System;
using Foxit.PDF;
using Foxit.PDF.Text;

class MyClass
{
    static void Main()
    {
        // Create a document and set it's properties
        Foxit.PDF.Document document = new Foxit.PDF.Document();
        document.Title = "PDF/X-1a Document";
        document.PdfVersion = PdfVersion.v1_4;

        // Create a page to add to the document
        Foxit.PDF.Page page = new Foxit.PDF.Page( PageSize.Letter, PageOrientation.Portrait, 54.0f );

        document.PdfXVersion = PdfXVersion.PDF_X_1a_2003;
        document.OutputIntents.Add( OutputIntent.USWebCoatedSwop );
        document.Trapped = Trapped.False;

        // Create a Label using a OpenType font and CMYK color
        string text = "PDF/X 1-a Document";
        OpenTypeFont openTypeFont = new OpenTypeFont( "verdana.otf" );
        Label label = new Label( text, 0, 0, 504, 100, openTypeFont, 18, TextAlign.Center, CmykColor.BlueViolet );

        // Add label to page
        page.Elements.Add( label );

        // Add page to document
        document.Pages.Add( page );

        // Save the PDF document
        document.Draw( @"C:\MyDocument.pdf" );
    }
}

member.example:
This example shows how to break the document into different sections.


            Imports System
            Imports Foxit.PDF

            Module MyModule

                Sub Main()

                    ' Create a PDF Document
                    Dim MyDocument As Document = New Document

                    'Create a template object and add a page numbering label
                    Dim MyTemplate As Template = New Template
                    MyTemplate.Elements.Add(New PageNumberingLabel("%%SP%% of %%ST%%", 0, 680, 512, 12, Font.Helvetica, 12, TextAlign.Center))

                    'Add the template to the document
                    MyDocument.Template = MyTemplate

                    'Begin the first section
                    MyDocument.Sections.Begin(NumberingStyle.RomanLowerCase)
                    'Add two pages
                    MyDocument.Pages.Add(New Page)     'Page 1
                    MyDocument.Pages.Add(New Page)     'Page 2

                    'Begin the second section
                    MyDocument.Sections.Begin(NumberingStyle.Numeric, MyTemplate)
                    'Add three pages
                    MyDocument.Pages.Add(New Page)     'Page 3
                    MyDocument.Pages.Add(New Page)     'page 4
                    MyDocument.Pages.Add(New Page)     'page 5

                    'Begin the third section specifying a section prefix
                    MyDocument.Sections.Begin(NumberingStyle.RomanLowerCase, "Appendix A - ")
                    'Add two pages
                    MyDocument.Pages.Add(New Page)     'page 6
                    MyDocument.Pages.Add(New Page)     'page 7

                    ' Save the PDF document
                    MyDocument.Draw("C:\MyDocument.pdf")

                End Sub
            End Module


            using System;
            using Foxit.PDF;

            class MyClass
            {
                static void Main()
                {
                    // Create a PDF Document
                    Document document = new Document();

                    //Create a template object and add a page numbering label
                    Template template = new Template();
                    template.Elements.Add( new PageNumberingLabel( "%%SP%% of %%ST%%", 0, 680, 512, 12, Font.Helvetica, 12, TextAlign.Center ) );

                    document.Template = template;

                    //Begin the first section
                    document.Sections.Begin( NumberingStyle.RomanLowerCase );
                    //Add two pages
                    document.Pages.Add( new Page() ); //Page 1
                    document.Pages.Add( new Page() ); //Page 2

                    //Begin the second section
                    document.Sections.Begin( NumberingStyle.Numeric, template );
                    //Add three pages
                    document.Pages.Add( new Page() ); //Page 3
                    document.Pages.Add( new Page() ); //page 4
                    document.Pages.Add( new Page() ); //page 5

                    //Begin the third section specifying a section prefix
                    document.Sections.Begin( NumberingStyle.RomanLowerCase, "Appendix A - " );
                    //Add two pages
                    document.Pages.Add( new Page() ); //page 6
                    document.Pages.Add( new Page() ); //page 7

                    // Save the PDF document
                    document.Draw( @"C:\MyDocument.pdf" );
                }
            }

This example shows how to break the document into different sections.


            Imports System
            Imports Foxit.PDF

            Module MyModule

                Sub Main()

                    ' Create a PDF Document
                    Dim MyDocument As Document = New Document

                    'Create a template object and add a page numbering label
